%spark

class LogInfo(val host: String, 
                val time: String, 
                val logLevel: String, 
                val httpVerb: String, 
                val resourcePath: String, 
                val protocol: String, 
                val returnCode: String, 
                val responseLength: String) {

}

%spark

def parse(line: String): Option[LogInfo] = {


    var splittedLine = line.replace("- ", "")
                            .replace("[", "")
                            .replace("]", "")
                            .replace("\"", "")
                            .split(" ")

    //some logs contain protocol, some don't -> Instert empty element at index 5
    if(splittedLine.length == 7){
        splittedLine = Array(splittedLine(0), splittedLine(1), splittedLine(2), splittedLine(3), splittedLine(4), "", splittedLine(5), splittedLine(6))
    }

    // return empty LogInfo if corrupt line
    if (splittedLine.length == 8) {
        return Option(new LogInfo(splittedLine(0), splittedLine(1), splittedLine(2), splittedLine(3), splittedLine(4), splittedLine(5), splittedLine(6), splittedLine(7)))
    }
    else {
        println("Unable to parse Line: "+ line)
        return None
    }

}
